INSERT INTO `douban_filmreview` VALUES (1203, '26661193', '5.2/10李鸿其长着一张高级的文艺脸，他似乎适合演一个病态的，颓废的，凄美的，堕落的青年，李一桐你懂吧…就跟谁搭都毫无存在感的感觉。演技挺好，奈何剧本差到浮夸，爱情亲情想要表达太多反而盈虚则满…总体感觉很差劲 毫无记忆共情点。', 508, '拥桑', '2020-08-25');
